sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          var rightnow = newGlideDateTime();
	      var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');

	      OverdueNeedItTasks.addquery('due date', '<=', 'rightnow');
	      overdueNeedIttasks.addquery('state', '!=', 3);
	      OverdueNeedIttasks.addquery('assigned_to', current.assigned_to);
	      OverdueNeedItTasks.query();

	      template.print("In addition to this Overdue NeedIt task" + (overdueNITtasks.GetRowcount()-1) + "More overdue NeedIt tasks");


})(current, template, email, email_action, event);
